package WS3DApp;

import java.util.Random;
import ws3dproxy.WS3DProxy;
import ws3dproxy.model.Creature;
import ws3dproxy.model.World;
import ws3dproxy.model.WorldPoint;

public class App {
    
    public Creature c;
    public World w;
    public int width;
    public int height;
    
    public App() {
        WS3DProxy proxy = new WS3DProxy();
        try {   
            w = World.getInstance();
            width = w.getEnvironmentWidth();
            height = w.getEnvironmentHeight();
            
            w.reset();
            World.createFood(0, 350, 75);
            World.createFood(0, 100, 220);
            World.createFood(0, 250, 210);
            c = proxy.createCreature(100,450,0);
            c.start();
            WorldPoint position = c.getPosition();
            double pitch = c.getPitch();
            double fuel = c.getFuel();    
        } catch (Exception e) {
            System.out.println("Erro capturado"); 
        }
    }
            

    public static void main(String[] args) {
        Random r = new Random();
        App app = new App();
        try {
            while(true) {
                app.c.moveto(4, r.nextInt(app.width),r.nextInt(app.height));
                Thread.sleep(5000);
            }
        } catch (Exception e) {
            System.out.println("Erro capturado"); 
        }
    }
}
